import { useScroll } from "framer-motion";
import { useState } from "react";

const Work = () => {
  const [images, setImages] = useState([
    {
      url: "https://cdn.prod.website-files.com/664dc8b6bc52b504509197f0/66b1608a4f57abd6f4bb1b06_cover-home-p-500.webp",
      top: "50%",
      left: "50%",
      isActive: false,
    },
    {
      url: "https://cdn.prod.website-files.com/664dc8b6bc52b504509197f0/669d6f1f10e02f2cb6320ad9_669ad38beb29c99f7dfe1353_mockup-v3%402x%202-p-1600-p-500.webp",
      top: "45%",
      left: "56%",
      isActive: false,
    },
    {
      url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBTgX1yEOlIEXwcbr8KZ_G1UlyUqs_EPIDLg&s",
      top: "56%",
      left: "44%",
      isActive: false,
    },
    {
      url: "data:image/jpeg;base64,",
      top: "60%",
      left: "53%",
      isActive: false,
    },
    {
      url: "data:image/jpeg;base64,",
      top: "43%",
      left: "40%",
      isActive: false,
    },
    {
      url: "data:image/jpeg;base64,",
      top: "65%",
      left: "55%",
      isActive: false,
    },
  ]);

  const { scrollYProgress } = useScroll();

  scrollYProgress.on("change", (data) => {
    function imagesShow(arr) {
      setImages((prev) =>
        prev.map((item, index) =>
          arr.indexOf(index) === -1
            ? { ...item, isActive: false }
            : { ...item, isActive: true }
        )
      );
    }

    const vals = Math.floor(data * 100);
    switch (vals) {
      case 0:
        imagesShow([]);

        break;
      case 1:
        imagesShow([0]);
        break;
      case 2:
        imagesShow([0, 1]);

        break;
      case 3:
        imagesShow([0, 1, 2]);

        break;
      case 4:
        imagesShow([0, 1, 2, 3]);

        break;
      case 5:
        imagesShow([0, 1, 2, 3, 4]);

        break;
      case 6:
        imagesShow([0, 1, 2, 3, 4, 5]);

        break;

      default:
        break;
    }
  });

  return (
    <div className="w-full mt-16">
      <div className="relative max-w-screen-xl mx-auto text-center">
        <h1 className="text-[28vw] leading-none tracking-tight font-medium select-none">
          Work
        </h1>
        <div className=" absolute top-0 w-full h-full ">
          {images.map(
            (elem, index) =>
              elem.isActive && (
                <img
                  key={index}
                  className="absolute w-72 rounded-lg  -translate-x-[50%] -translate-y-[50%]"
                  style={{ top: elem.top, left: elem.left }}
                  src={elem.url}
                />
              )
          )}
        </div>
      </div>
    </div>
  );
};

export default Work;
